import { openDB } from "idb";
import JSZip from "jszip";

const DB_NAME = "h5p-storage";
const STORE_NAME = "files";

async function getDB() {
    return openDB(DB_NAME, 1, {
        upgrade(db) {
            if (!db.objectStoreNames.contains(STORE_NAME)) {
                db.createObjectStore(STORE_NAME);
            }
        },
    });
}

export async function saveH5P(file) {
    const db = await getDB();
    const zip = new JSZip();
    const zipContent = await zip.loadAsync(file);

    // 1. Extract all blobs first to avoid transaction inactivity
    const files = [];
    const extractionPromises = [];

    zipContent.forEach((relativePath, zipEntry) => {
        if (!zipEntry.dir) {
            extractionPromises.push(
                zipEntry.async("blob").then((blob) => {
                    files.push({ path: relativePath, blob });
                })
            );
        }
    });

    await Promise.all(extractionPromises);

    // 2. Clear old data and store new files
    const tx = db.transaction(STORE_NAME, "readwrite");
    const store = tx.objectStore(STORE_NAME);

    await store.clear();

    const id = "content-" + Date.now();

    for (const { path, blob } of files) {
        // We await each put to ensure order (though irrelevant here) and tx safety
        // Using Promise.all with store.put is also fine if tx is active,
        // but iteration is safer against race conditions in some IDB implementations.
        store.put(blob, path);
    }

    await tx.done;

    return id;
}
